import React from "react";
import ReactDOM from "react-dom";

// UI
import { Row, Col, Layout, Input, Menu } from "antd";
import "antd/dist/antd.css";

// Local
import "./index.css";
import UserCard from "./components/UserCard";

const { Header, Footer, Sider, Content } = Layout;
const { Search } = Input;

class App extends React.Component {
  state = {
    users: [],
    searchQuery: ""
  };

  componentDidMount() {
    fetch(`https://api.github.com/users/vinnihoke`)
      .then(response => response.json())
      .then(user => this.setState({ users: [user] }))
      .catch(err => console.log(err));
  }

  // searchSubmit() {
  //   fetch(`https://api.github.com/users/${this.searchQuery}`)
  //     .then(response => response.json())
  //     .then(user => this.setState({ ...this.users, users: [user] }))
  //     .catch(err => console.log(err));
  // }

  render() {
    return (
      <Layout>
        <Content>
          <Row id="Navbar">
            <Col span={8}>
              <div className="logo">Github User Card</div>
            </Col>
            <Col span={8} offset={8}>
              Add to favorites
              <Search
                onSearch={value => this.setState({ searchQuery: value })}
                // onPressEnter={() => this.searchSubmit()}
              ></Search>
              {console.log(this.searchQuery)}
            </Col>
          </Row>
        </Content>
        <Content>
          <Row
            className="App"
            type="flex"
            justify="center"
            style={{
              backgroundColor: "lightcyan"
            }}
          >
            {console.log(this.state.users)}
            <Col
              sm={24}
              md={12}
              lg={8}
              style={{ backgroundColor: "lightcyan" }}
            >
              {this.state.users.map(user => (
                <UserCard userData={user} key={user.id} />
              ))}
            </Col>
          </Row>
        </Content>
      </Layout>
    );
  }
}

export default App;

ReactDOM.render(<App />, document.getElementById("root"));
